package Iniciante;

import java.util.Scanner;

public class I_1003_SomaSimples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a;
		int b;
		int soma;
		
		Scanner sc = new Scanner(System.in);
		
		a = sc.nextInt();
		b = sc.nextInt();
		soma = a + b;
		
		System.out.println("SOMA = "+soma);
		

	}

}
